import React, { useState, useEffect } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { AppBar, Avatar, Button, Toolbar, Typography } from "@material-ui/core";
import memoriesLogo from '../../images/memoriesLogo.jpg';
import memoriesText from '../../images/memoriesText.png';
import useStyles from "./styles";
import { useDispatch } from "react-redux";
import { jwtDecode } from 'jwt-decode';

const defaultAvatar = 'path/to/default-avatar.jpg'; // Path to a default placeholder image

const Navbar = () => {
  const classes = useStyles();
  const [user, setUser] = useState(JSON.parse(localStorage.getItem('profile')));
  const location = useLocation();
  const dispatch = useDispatch();
  const history = useNavigate();

  const logout = () => {
    dispatch({ type: "LOGOUT" });
    history("/");
    setUser(null);
  };

  useEffect(() => {
    const token = user?.token;
    if (token) { 
      const decodedToken = jwtDecode(token);
      if (decodedToken.exp * 1000 < new Date().getTime()) {
        logout();
      }
    }
  }, [location]);

  return (
    <AppBar className={classes.appBar} position="static" color="inherit">
      <Toolbar>
        <Typography variant="h6" className={classes.title}>
          <img src={memoriesLogo} alt="memoriesLogo" height="25" />
          <img src={memoriesText} alt="memoriesText" height="25" />
        </Typography>
        {user ? (
          <div className={classes.profile}>
            <Avatar className={classes.purple} alt={user.result.name} src={user.result.picturePath ? `/images/${user.result.picturePath}` : defaultAvatar} />
            <Typography variant="h6">{user.result.name}</Typography>
            <Button variant="contained" className={classes.logout} onClick={logout}>
              Logout
            </Button>
          </div>
        ) : (
          <Button component={Link} to="/auth" variant="contained" color="primary">
            Sign In
          </Button>
        )}
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
